import { useCallback, useEffect, useState } from "react"
import { useCustomFetch } from "src/hooks/useCustomFetch"
import { SetTransactionApprovalParams, Transaction } from "src/utils/types"
import { TransactionPane } from "./TransactionPane"
import { SetTransactionApprovalFunction, TransactionsComponent } from "./types"

export const Transactions: TransactionsComponent = ({ transactions }) => {
  const { fetchWithoutCache, loading } = useCustomFetch()
  const [transactionsState, setTransactionsState] = useState<Transaction[] | null>(transactions)

  const setTransactionApproval = useCallback<SetTransactionApprovalFunction>(
    async ({ transactionId, newValue }) => {
      await fetchWithoutCache<void, SetTransactionApprovalParams>("setTransactionApproval", {
        transactionId,
        value: newValue,
      })
    },
    [fetchWithoutCache]
  )
  useEffect(() => {
    setTransactionsState(transactions)
  },[transactions])

  if (transactions === null || transactionsState === null) {
    return <div className="RampLoading--container">Loading...</div>
  }
  let sort: string = "b"

  const sortEmplooyeeByName = () => {
    const sortByName = () => {
      return [...transactionsState].sort((a, b) => {
        const firstNameA = a.employee.firstName.toUpperCase();
        const firstNameB = b.employee.firstName.toUpperCase();
        return sort === "a" ? firstNameA.localeCompare(firstNameB) : firstNameB.localeCompare(firstNameA)
      })
    };
    sort = sort === "a" ? "b" : "a"
    console.log(11111111111111,sortByName())
    // setTransactionApproval(sortByName())
  }

  console.log(11111111111111,sortEmplooyeeByName())

  return (
    <div data-testid="transaction-container">
      <button
        className="RampButton">
          {/* onClick={ () => sortEmplooyeeByName()} */}
        Sort by Name â‡…
      </button>
      {transactionsState.map((transaction) => (
        <TransactionPane
          key={transaction.id}
          transaction={transaction}
          loading={loading}
          setTransactionApproval={setTransactionApproval}
        />
      ))}
    </div>
  )
}
